import { Component, OnInit } from '@angular/core'; 
import { IArticle } from 'src/app/entities/article/article.model';
import { ArticleService } from 'src/app/entities/article/service/article.service';
import * as L from 'leaflet'; 
import { IClub } from 'src/app/entities/club/club.model';
import { Router } from '@angular/router';
import Geocoder from 'leaflet-control-geocoder';
import * as ELG from 'esri-leaflet-geocoder';
import { GeoSearchControl, OpenStreetMapProvider } from 'leaflet-geosearch';
import { HomeClientService } from './clienthome.service';
import { LoginService } from 'src/app/auth/login.service';
@Component({
  selector: 'app-clienthome',
  templateUrl: './clienthome.component.html',
  styleUrls: ['./clienthome.component.css']
})
export class ClienthomeComponent implements OnInit {
  title = 'Google Maps';
  article: IArticle[] | undefined;
  clubs: IClub[] = [];
  private map: any;
  

  private lat: number | any;
  private lon: number | any;

  constructor(
    private service: ArticleService,
    private dashserv: HomeClientService ,
    private router : Router ,
    public loginService : LoginService
  ) {}

  private async initMap(): Promise<void> {
   
    this.dashserv.afficheClub().subscribe((data) => {
      this.clubs =data ;
       for (let b of this.clubs) {
         this.lat = b.latitude;
         this.lon  = b.longitude;
          let mark =L.marker([JSON.parse(this.lat), JSON.parse(this.lon)], {
            icon: century21icon,
            draggable: false,
            
          }).addTo(this.map).on('click',()=>{
            this.router.navigate([`/club/${b.id}/home`]);
          });
       }
       console.log(this.lat)
    });
   
    
    this.map = L.map('map', {
      center: [39.8282, -98.5795],
      zoom: 3,
    });
    
    const tiles = L.tileLayer(
      'https://{s}.tile.openstreetmap.org/{z}/{x}/{y}.png',
      {
        maxZoom: 18,
        minZoom: 3,
        attribution:
          '&copy; <a href="http://www.openstreetmap.org/copyright">OpenStreetMap</a>',
      }
    ); 
    let century21icon = L.icon({
      iconUrl:
        'data:image/png;base64,',

      iconSize: [20, 20],
    });
    tiles.addTo(this.map);
   

    //Layer layerController

    //google street
    let googleStreets = L.tileLayer(
      'http://{s}.google.com/vt/lyrs=m&x={x}&y={y}&z={z}',
      {
        maxZoom: 20,
        subdomains: ['mt0', 'mt1', 'mt2', 'mt3'],
      }
    );
    //google hybrid
    let googleHybrid = L.tileLayer(
      'http://{s}.google.com/vt/lyrs=s,h&x={x}&y={y}&z={z}',
      {
        maxZoom: 20,
        subdomains: ['mt0', 'mt1', 'mt2', 'mt3'],
      }
    );
    //darkMap
    let darkMap = L.tileLayer(
      'https://{s}.basemaps.cartocdn.com/dark_all/{z}/{x}/{y}{r}.png',
      {
        attribution:
          '&copy; <a href="https://www.openstreetmap.org/copyright">OpenStreetMap</a> contributors &copy; <a href="https://carto.com/attributions">CARTO</a>',
        subdomains: 'abcd',
        maxZoom: 19,
      }
    );
    //Satellite
    let googleSat = L.tileLayer(
      'http://{s}.google.com/vt/lyrs=s&x={x}&y={y}&z={z}',
      {
        maxZoom: 20,
        subdomains: ['mt0', 'mt1', 'mt2', 'mt3'],
      }
    );
    //OSM
    let osm = L.tileLayer(
      'https://{s}.tile.openstreetmap.org/{z}/{x}/{y}.png',
      {
        attribution:
          '&copy; <a href="https://www.openstreetmap.org/copyright">OpenStreetMap</a> contributors',
      }
    );
    //Teerrain
    let googleTerrain = L.tileLayer(
      'http://{s}.google.com/vt/lyrs=p&x={x}&y={y}&z={z}',
      {
        maxZoom: 20,
        subdomains: ['mt0', 'mt1', 'mt2', 'mt3'],
      }
    );
    var layer = new (L.tileLayer as any)(
      'http://{s}.tile.openstreetmap.org/{z}/{x}/{y}.png',
      {
        noWrap: true,
      }
    );
    layer.addTo(this.map)
    let badeMaps = {
      hybrid: googleHybrid,
      Satellite: googleSat,
      terrain: googleTerrain,
      dark: darkMap,
      osm: osm,
    };
    let overLayMaps = {
      Marker: century21icon,
    }; 
    L.control.layers(badeMaps).addTo(this.map);
    const provider = new OpenStreetMapProvider(
      
    );
   const searchControl = new (GeoSearchControl as any)({
     provider: provider,
     autoComplete: true,
     showPopup: false,
     autoClose: false,
     style: 'button',
     icon: new L.Icon.Default(),
   });
   this.map.addControl(searchControl);
  }

  ngOnInit(): void {
    this.initMap();
  }
  loadAll(): void {
    this.service.list().subscribe((data) => {
      this.article = data;
      console.warn(this.article);
    });
  }
}
 